["init"] = {
["custom"] = "---- Constants ----\n\nlocal ascentSpellId = 372610\nlocal thrillBuffId = 377234\nlocal maxPassiveGlideSpeed = 65\nlocal slowSkyridingRatio = 705/830\nlocal maxSamples = 2\nlocal ascentDuration = 3.5\nlocal updatePeriod = 1/20\nlocal fastFlyingZones = {\n  [2444] = true, -- Dragon Isles\n  [2454] = true, -- Zaralek Cavern\n  [2548] = true, -- Emerald Dream\n\n  [2516] = true, -- Nokhud Offensive\n\n  [2522] = true, -- Vault of the Incarnates\n  [2569] = true, -- Aberrus, the Shadowed Crucible\n}\n\n---- Parameters ----\n\nlocal showSpeed = aura_env.config.speedshow\nlocal hideBlizz = aura_env.config.hideblizz\n\nlocal speedTextFormat, speedTextFactor = \"\", 1\nif aura_env.config.speedunits == 1 then\n  speedTextFormat = aura_env.config.speedshowunits and \"%.1fyd/s\" or \"%.1f\"\nelse\n  speedTextFormat = aura_env.config.speedshowunits and \"%.0f%%\" or \"%.0f\"\n  speedTextFactor = 100 / BASE_MOVEMENT_SPEED\nend\n\n---- Variables ----\n\nlocal active = false\nlocal updateHandle = nil\nlocal ascentStart = 0\nlocal samples = 0\nlocal lastSpeed, lastT = 0, 0\nlocal smoothAccel = 0\nlocal isSlowSkyriding = true\n\n---- Trigger 1 ----\n\n-- Events:\n--   UNIT_SPELLCAST_SUCCEEDED:player\n--   DMUI_DRAGONRIDING_UPDATE\n\nlocal function setActive(allstates, state)\n  active = state\n  C_Timer.After(0, function()\n    WeakAuras.ScanEvents(\"DMUI_DRAGONRIDING_SHOW\", state)\n  end)\n\n  if active then\n    if hideBlizz and UIWidgetPowerBarContainerFrame:IsVisible() then\n      aura_env.reshow = true\n      UIWidgetPowerBarContainerFrame:Hide()\n    end\n\n    if not updateHandle then\n      updateHandle = C_Timer.NewTicker(updatePeriod, function()\n          if active then\n            WeakAuras.ScanEvents(\"DMUI_DRAGONRIDING_UPDATE\", true)\n          end\n      end)\n    end\n\n    if not allstates[\"\"] then\n      allstates[\"\"] = {\n        show = true,\n        changed = true,\n        progressType = \"static\",\n        value = 0,\n        accel = 0,\n        total = 100,\n        boosting = false,\n        thrill = false,\n        speedtext = \"\",\n        angle = \"\",\n      }\n      return true\n    end\n  else\n    if updateHandle then\n      updateHandle:Cancel()\n      updateHandle = nil\n    end\n\n    if allstates[\"\"] then\n      allstates[\"\"].show = false\n      allstates[\"\"].changed = true\n      return true\n    end\n  end\nend\n\naura_env.cancelCallback = function()\n  if updateHandle then\n    updateHandle:Cancel()\n    updateHandle = nil\n  end\nend\n\naura_env.trigger1 = function(allstates, event, _, _, spellId)\n  if event ~= \"DMUI_DRAGONRIDING_UDPATE\" then\n    if event == \"OPTIONS\" then\n      return setActive(allstates, false)\n    end\n\n    if event == \"STATUS\" then\n      isSlowSkyriding = not fastFlyingZones[select(8, GetInstanceInfo())]\n      return setActive(allstates, true)\n    end\n\n    -- Detect ascent boost\n\n    if event == \"UNIT_SPELLCAST_SUCCEEDED\" then\n      if spellId == ascentSpellId then\n        ascentStart = GetTime()\n      end\n      return false\n    end\n  end\n\n  -- Time\n\n  local time = GetTime()\n  local dt = time - lastT\n  lastT = time\n\n  if not allstates or not allstates[\"\"] then return false end\n\n  -- Get flying speed\n\n  local isGliding, _, forwardSpeed = C_PlayerInfo.GetGlidingInfo()\n  local speed = forwardSpeed\n\n  local thrill = C_UnitAuras.GetPlayerAuraBySpellID(thrillBuffId)\n  local boosting = thrill and time < ascentStart + ascentDuration\n\n  local adjustedSpeed = speed\n  if isSlowSkyriding then\n    adjustedSpeed = adjustedSpeed / slowSkyridingRatio\n  end\n\n  -- Compute smooth acceleration\n\n  samples = math.min(maxSamples, samples + 1)\n  local lastWeight = (samples - 1) / samples\n  local newWeight = 1 / samples\n\n  local newAccel = (adjustedSpeed - lastSpeed) / dt\n  lastSpeed = adjustedSpeed\n\n  smoothAccel = smoothAccel * lastWeight + newAccel * newWeight\n\n  if adjustedSpeed >= maxPassiveGlideSpeed or not isGliding then\n    smoothAccel = 0 -- Don't track when boosting or on ground\n    samples = 0\n  end\n\n  WeakAuras.ScanEvents(\"DMUI_DRAGONRIDING_ACCEL\", smoothAccel)\n\n  -- Update display variables\n  local s = allstates[\"\"]\n  s.changed = true\n  s.value = adjustedSpeed\n  s.boosting = boosting\n  s.thrill = not not thrill\n  s.gliding = isGliding\n  s.accel = smoothAccel\n  if showSpeed then\n    s.speedtext = speed < 1 and \"\" or string.format(speedTextFormat, speed * speedTextFactor)\n  end\n\n  return true\nend",
