["init"] = {
["custom"] = "local blocked_realms = {\n    [\"Quel'Thalas\"] = 1,\n    [\"Drakkari\"] = 1,\n    [\"Ragnaros\"] = 1,\n    [\"Goldrinn\"] = 1,\n    [\"Gallywix\"] = 1,\n    [\"Azralon\"] = 1,\n    [\"Nemesis\"] = 1,\n    [\"Tol Barad\"] = 1,\n}\n\naura_env.UpdateApplicantMember = function(member, appID, memberIdx, ...) \n    local fullName = C_LFGList.GetApplicantMemberInfo(appID, memberIdx);\n    local name, realm = (\"-\"):split(fullName);\n    \n    if not realm then realm = GetRealmName() end\n    if blocked_realms[realm]then \n\n        if member.InviteButton then \n            member.InviteButton:Hide()\n        end\n\n        if member.InviteButtonSmall then \n            member.InviteButtonSmall:Hide()\n        end\n\n        member.Name:SetText(\"|cFFff0000\"..realm:upper()..\"|r\")\n        member.RoleIcon1:Hide()\n        member.RoleIcon2:Hide()\n        member.RoleIcon3:Hide()\n        member.ItemLevel:Hide()\n        member.DungeonScore:Hide()\n    end\nend\n\naura_env.UpdateApplicant = function(button, id)\n    local appInfo = C_LFGList.GetApplicantInfo(id)\n    for i = 1, appInfo.numMembers do\n        local fullName = C_LFGList.GetApplicantMemberInfo(id, i)\n        local name, realm = (\"-\"):split(fullName);\n        if not realm then realm = GetRealmName() end\n        \n        if blocked_realms[realm]then \n            if button.InviteButton then \n                button.InviteButton:Hide()\n            end\n    \n            if button.InviteButtonSmall then \n                button.InviteButtonSmall:Hide()\n            end\n        end\n    end\nend\n\naura_env.SearchEntry_Update = function(group)\n    local result = C_LFGList.GetSearchResultInfo(group.resultID)\n    if not result.leaderName then return end\n    \n    local name, realm = (\"-\"):split(result.leaderName);\n    \n    if not realm then realm = GetRealmName() end\n    if blocked_realms[realm]then \n        local gName = group.Name:GetText()\n        group.Name:SetText(\"[|cFFFF0000\"..realm:upper()..\"|r] \"..gName)\n    end\nend\n\nlocal is_loaded = _G[\"BeanBlockerLoaded\"]\n\nif not is_loaded then \n    hooksecurefunc(\"LFGListApplicationViewer_UpdateApplicantMember\", aura_env.UpdateApplicantMember);\n    hooksecurefunc(\"LFGListApplicationViewer_UpdateApplicant\", aura_env.UpdateApplicant);\n    hooksecurefunc(\"LFGListSearchEntry_Update\", aura_env.SearchEntry_Update)\n    _G[\"BeanBlockerLoaded\"] = true\nend\n\n\n",
["do_custom"] = true,
