["subeventPrefix"] = "SPELL",
["custom"] = "function(_weakAuraEvent, _event, _id, _message, _expirationTime)\n    if _weakAuraEvent ~= \"BOSSMOD_TIMELINE\" then\n        return\n    end\n    \n    if _event == \"START\" then\n        if not _id then\n            return\n        end\n        \n        aura_env.timers[_id] = _expirationTime\n        \n        C_Timer.After(_expirationTime - GetTime(), function() WeakAuras.ScanEvents(\"BOSSMOD_TIMELINE_EXPIRATION\", \"\", _id) end)\n        \n        return true\n    end\nend",
["debuffType"] = "HELPFUL",
},
["untrigger"] = {
["custom"] = "function(_weakAuraEvent, _event, _id)\n    if _weakAuraEvent ~= \"BOSSMOD_TIMELINE\" and _weakAuraEvent ~= \"BOSSMOD_TIMELINE_EXPIRATION\" then\n        return\n    end\n    \n    if _weakAuraEvent == \"BOSSMOD_TIMELINE\" then\n        if _event == \"STOP\" then\n            if not _id then\n                return\n            end\n            \n            aura_env.timers[_id] = nil\n        elseif _event == \"STOP_ALL\" then\n            for id, expiration in pairs(aura_env.timers) do\n                if not _id or tostring(_id) == strsplit(\"^\", id) then\n                    aura_env.timers[id] = nil\n                end\n            end\n        else\n            return false\n        end\n    end\n    \n    local hasTimer = false\n    local now = GetTime()\n    \n    for id, expiration in pairs(aura_env.timers) do\n        if expiration <= now then\n            aura_env.timers[id] = nil\n        else\n            hasTimer = true\n        end\n    end\n    \n    return not hasTimer\nend",
