["custom"] = "function(event, version, ...)\n    if (event == \"AutomarkSettingsResponse\") then \n        local type = ...;\n        local settings = aura_env.Settings[type];\n        local playerName = aura_env.GetPlayerName();\n        \n        if type == \"Automark\" then\n            if version == 1 then \n                local _, automark, makeAvailable, modifier, range, announce = ...;\n                \n                settings.AutomarkOption = automark;\n                settings.MakeAvailable = makeAvailable;\n                settings.Modifier = modifier;\n                settings.RangeLimiter = range;\n                settings.Version = version;\n                settings.Announce = announce;\n            end\n        elseif type == \"MarkOrder\" then \n            if version == 1 then \n                local _, skull, cross, square, moon, triangle, diamond, circle, star = ...;\n                \n                for i = 1, 8 do \n                    for k, mark in ipairs({star, circle, diamond, triangle, moon, square, cross, skull}) do\n                        if i == mark.Order and mark.Enable then\n                            settings[i] = {\n                                [\"MarkId\"] = k,\n                                [\"Enable\"] = mark.Enable,\n                            }\n                        end\n                    end\n                end \n                \n                settings.Version = version;\n            end\n        elseif type == \"PlayerMarks\" then \n            if version == 1 then \n                local _, tank, healer = ...;\n                \n                settings.Tank = tank;\n                settings.Healer = healer;\n                settings.Version = version;\n            end\n        elseif type == \"PartySettings\" then \n            if version == 1 then \n                local _, tank, healer, damager, leader = ...;\n                \n                settings.TANK = tank;\n                settings.HEALER = healer;\n                settings.DAMAGER = damager;\n                settings.Leader = leader;\n                settings.Version = version;\n            end\n        end\n    end\n    if event == \"AutomarkGetMobsResponse\" then\n        if version == 1 then\n            --\n            local NpcId, AutomarkOption, PriorityMarks, RangeLimit, WhenNotAvailable, WhenUnitDie, MakeAvailableOn = ...;\n            \n            if AutomarkOption == 5 then \n                AutomarkOption = aura_env.Settings.Automark.AutomarkOption;\n            else\n                AutomarkOption = AutomarkOption-1;\n            end\n            \n            aura_env.Mobs[NpcId] = {\n                [\"AutomarkOption\"] = AutomarkOption,\n                [\"Range\"] = RangeLimit,\n                [\"UnitDied\"] = WhenUnitDie,\n                [\"MakeAvailable\"] = MakeAvailableOn,\n                [\"Prio\"] = PriorityMarks,\n                [\"NotAvailable\"] = WhenNotAvailable,\n            }\n        end\n    end\nend",
