["init"] = {
["custom"] = "local travelDistance = 227\nif aura_env.config.reverse then\n    travelDistance = 232\nend\n\naura_env.data = { spacing = 25, travelDistance = travelDistance }\n\nif BigWigsLoader then\n    local BossModTimelineBarCreatedCallback = function(_event, _module, _bar)\n        _bar.candyBarBar:Hide()\n        _bar.candyBarIconFrame:Hide()\n    end\n    \n    local BossModTimelineBarEmphasizedCallback = function(_event, _module, _bar)\n        _bar.candyBarBar:Hide()\n        _bar.candyBarIconFrame:Hide()\n    end\n    \n    local BossModTimelineOnBossDisableCallback = function(_event, _module)\n        WeakAuras.ScanEvents(\"BOSSMOD_TIMELINE\", \"STOP_ALL\", _module)\n    end\n    \n    local BossModTimelineFrameCreated = function(_event, _frame, _name)\n        if _name ~= \"QueueTimer\" then\n            return\n        end\n        \n        _frame:Hide()\n    end\n    \n    local BossModTimelineOnPluginDisableCallback = function(_event, _module)\n        WeakAuras.ScanEvents(\"BOSSMOD_TIMELINE\", \"STOP_ALL\", _module)\n    end\n    \n    local BossModTimelineStartBarCallback = function(_event, _module, _spellId, _message, _duration, _icon)\n        local red, green, blue, alpha = BigWigs:GetPlugin(\"Colors\"):GetColor(\"barText\", _module, _spellId)\n        \n        WeakAuras.ScanEvents(\"BOSSMOD_TIMELINE\", \"START\", tostring(_module) .. \"^\" .. (_message or \"\"), _message, GetTime() + (_duration or 0), _icon, { red, green, blue, alpha })\n    end\n    \n    local BossModTimelineStopBarCallback = function(_event, _module, _message)\n        WeakAuras.ScanEvents(\"BOSSMOD_TIMELINE\", \"STOP\", tostring(_module) .. \"^\" .. (_message or \"\"), _message, 0)\n    end\n    \n    local BossModTimelineStopBarsCallback = function(_event, _module)\n        WeakAuras.ScanEvents(\"BOSSMOD_TIMELINE\", \"STOP_ALL\", _module)\n    end\n    \n    local BIGWIGS_TIMELINE_CallbackObject = {}\n    \n    if aura_env.config.hideBossModBars then\n        BigWigsLoader.RegisterMessage(BIGWIGS_TIMELINE_CallbackObject, \"BigWigs_BarCreated\", BossModTimelineBarCreatedCallback)\n        BigWigsLoader.RegisterMessage(BIGWIGS_TIMELINE_CallbackObject, \"BigWigs_BarEmphasized\", BossModTimelineBarEmphasizedCallback)\n    end\n    \n    BigWigsLoader.RegisterMessage(BIGWIGS_TIMELINE_CallbackObject, \"BigWigs_FrameCreated\", BossModTimelineFrameCreated)\n    BigWigsLoader.RegisterMessage(BIGWIGS_TIMELINE_CallbackObject, \"BigWigs_OnBossDisable\", BossModTimelineOnBossDisableCallback)\n    BigWigsLoader.RegisterMessage(BIGWIGS_TIMELINE_CallbackObject, \"BigWigs_OnPluginDisable\", BossModTimelineOnPluginDisableCallback)\n    BigWigsLoader.RegisterMessage(BIGWIGS_TIMELINE_CallbackObject, \"BigWigs_StartBar\", BossModTimelineStartBarCallback)\n    BigWigsLoader.RegisterMessage(BIGWIGS_TIMELINE_CallbackObject, \"BigWigs_StopBar\", BossModTimelineStopBarCallback)\n    BigWigsLoader.RegisterMessage(BIGWIGS_TIMELINE_CallbackObject, \"BigWigs_StopBars\", BossModTimelineStopBarsCallback)\nend\n\nif DBM then\n    local BossModTimelineStartBarCallback = function(_event, _id, _message, _duration, _icon, _, _, _colorType)\n        DBT:CancelAllBars()\n        \n        local red, green, blue = DBT:GetColorForType(_colorType)\n        \n        WeakAuras.ScanEvents(\"BOSSMOD_TIMELINE\", \"START\", _id, _message, GetTime() + (_duration or 0), _icon, { red, green, blue })\n    end\n    \n    local BossModTimelineStopBarCallback = function(_event, _id, _message)\n        WeakAuras.ScanEvents(\"BOSSMOD_TIMELINE\", \"STOP\", _id, _message, 0)\n    end\n    \n    DBM:RegisterCallback(\"DBM_TimerStart\", BossModTimelineStartBarCallback)\n    DBM:RegisterCallback(\"DBM_TimerStop\", BossModTimelineStopBarCallback)\nend",
