["custom"] = "function(_,message,sender,_,_,_,_,_,_,_,_,_,arg12)\n    if not (message == nil) then\n        \n        local playerName = UnitName(\"player\")    \n        local keyword = aura_env.config.chatPingKeyword1\n        \n        -- Only launches if the Player Name Toggle option is ENABLED\n        if (aura_env.config.chatPingNameToggle) then\n            if string.match(string.lower(message), playerName) then\n                local _,engClass,_,_,_,name,realm = GetPlayerInfoByGUID(arg12)\n                rPerc, gPerc, bPerc, argbHex = GetClassColor(engClass)\n                chatPingNotificationMsg = (\"[|c\" .. argbHex .. sender .. \"|r]: \" .. message)        \n                \n                return chatPingNotificationMsg\n            end        \n        end\n        \n        \n        -- Launches if Keyword is found in chat message.\n        if string.match(string.lower(message), keyword) then    \n            local _,engClass,_,_,_,name,realm = GetPlayerInfoByGUID(arg12)\n            rPerc, gPerc, bPerc, argbHex = GetClassColor(engClass)\n            chatPingNotificationMsg = (\"[|c\" .. argbHex .. sender .. \"|r]: \" .. message)        \n            \n            return chatPingNotificationMsg\n        end\n    else\n        return false        \n    end\nend\n\n\n",
