["custom"] = "aura_env.deaths = {}\n\naura_env.formattime = function(time, MS, dec)\n    if time then\n        local timeMin = math.floor(time / 60)\n        local timeSec = math.floor(time - (timeMin*60))\n        if timeMin < 10 then\n            timeMin = (\"0%d\"):format(timeMin)\n        end\n        if timeSec < 10 then\n            timeSec = (\"0%d\"):format(timeSec)\n        end        \n        return (\"%s:%s\"):format(timeMin, timeSec)\n    end\nend\n\naura_env.tooltip = function(deaths, timelost)\n    timelost = aura_env.formattime(timelost)\n    local text = \"Deaths: \"..deaths..\"\\nTime lost: \"..timelost..\"\\n\"\n    local list = {}\n    for u, num in pairs(aura_env.deaths) do\n        local name = NSAPI and NSAPI:Shorten(u, 8) or WA_ClassColorName(u)\n        table.insert(list, {name, num})\n    end\n    table.sort(list, \n        function(a, b)\n            if a[2] == b[2] then -- sort by name if deathcount is equal\n                return a[1] > b[1]\n            else -- otherwise sort by death count\n                return a[2] > b[2] \n            end\n            \n    end)\n    for _, v in ipairs(list) do\n        text = text..v[1]..\" \"..v[2]..\"\\n\"\n    end\n    return text\nend\n\n\naura_env.maptoID = { -- MapChallengeMode = JournalInstance\n    -- Cata\n    [438] = {68, \"Vortex Pinnacle\"},\n    [456] = {65, \"Throne\"},\n    [507] = {71, \"Grim Batol\"},\n    -- MoP\n    [2] = {313, \"Jade Serpent\"},\n    [56] = {302, \"Stromsout\"},\n    [57] = {303, \"Setting Sun\"},\n    [58] = {312, \"Shadow-Pan\"},\n    [59] = {324, \"Niuzao\"},\n    [60] = {321, \"Mogu'shan\"},\n    [76] = {246, \"Scholomance\"},\n    [77] = {311, \"Scarlet Halls\"},\n    [78] = {316, \"Monastery\"},\n    \n    -- WoD\n    [161] = {476, \"Skyreach\"},\n    [163] = {385, \"Slag Mines\"},\n    [164] = {547, \"Auchindoun\"},\n    [165] = {537, \"Shadowmoon\"},\n    [166] = {536, \"Grimrail\"},\n    [167] = {559, \"UBRS\"},\n    [168] = {556, \"Everbloom\"},\n   