["custom"] = "function(s, e, ...)\n    if e == \"OPTIONS\" then\n        local mapID = C_ChallengeMode.GetActiveChallengeMapID();\n        if mapID then\n            aura_env.mapID = mapID\n        else\n            mapID = aura_env.mapID\n        end\n        if mapID and aura_env.affixinfo then\n            local deaths, timelost = C_ChallengeMode.GetDeathCount()\n            s[\"\"] = {\n                show = true,\n                deathcount = deaths,\n                progressType = \"static\",\n                icon = \"\",\n                tooltip = aura_env.tooltip(deaths, timelost),\n                changed = true,\n                keylevel = \"+\"..aura_env.level,\n                dungeonname = aura_env.dungeon,\n                affixes = aura_env.affixinfo,\n                \n            }\n            return true            \n            \n        else\n            local keyfake = \"\"\n            for i=1, 4 do\n                keyfake = keyfake..\"\\124T\"..select(i, strsplit(\" \", \"236401 1035055 451169 1385910\"))..\":13:13:\"..1-i..\":0:64:64:6:60:6:60\\124t\"\n            end                \n            s[\"\"] = {\n                show = true,\n                progressType = \"static\",\n                keylevel = \"+\"..30,\n                dungeonname = \"Halls of Valor\",\n                affixes = keyfake,\n                deathcount = 20,\n                tooltip = aura_env.tooltip(20, 100),\n                changed = true,\n            }\n            return true\n        end\n        \n    elseif e == \"CHALLENGE_MODE_START\" or e == \"STATUS\" then\n        if e == \"CHALLENGE_MODE_START\" then\n            aura_env.deaths = {}\n        end\n        local mapID = C_ChallengeMode.GetActiveChallengeMapID()\n        aura_env.level, aura_env.affixes = C_ChallengeMode.GetActiveKeystoneInfo()\n        aura_env.dungeon = mapID and aura_env.maptoID[mapID] and aura_env.maptoID[mapID][2] or \"\"\n        aura_env.icon = \"\"\n        for _, v in pairs(aura_env.affixes) do\n            if aura_env.icon == \"\" then\n                aura_env.icon = select(3, C_ChallengeMode.GetAffixInfo(v))\n            else\n                aura_env.icon = aura_env.icon..\" \"..select(3, C_ChallengeMode.GetAffixInfo(v))\n            end\n        end\n        aura_env.affixinfo = \"\"\n        for i=1, 4 do\n            if select(i, strsplit(\" \", aura_env.icon)) then\n                aura_env.affixinfo = aura_env.affixinfo..\"\\124T\"..select(i, strsplit(\" \", aura_env.icon))..\":12:12:\"..1-i..\":0:64:64:6:60:6:60\\124t\"\n            end\n        end\n        local deaths, timelost = C_ChallengeMode.GetDeathCount()\n        deaths = deaths or 0\n        timelost = timelost  or 0\n        s[\"\"] = {\n            show = true,\n            deathcount = deaths,\n            progressType = \"static\",\n            tooltip = aura_env.tooltip(deaths, timelost),\n            changed = true,\n            keylevel = \"+\"..aura_env.level,\n            dungeonname = aura_env.dungeon,\n            affixes = aura_env.affixinfo,\n            \n        }\n        return true\n        \n        \n    elseif e == \"CHALLENGE_MODE_DEATH_COUNT_UPDATED\" and s[\"\"] then\n        \n        local deaths, timelost = C_ChallengeMode.GetDeathCount()\n        deaths = deaths or 0\n        timelost = timelost  or 0\n        s[\"\"].deathcount = deaths\n        s[\"\"].tooltip = aura_env.tooltip(deaths, timelost)\n        s[\"\"].changed = true\n        return true\n        \n    elseif e == \"COMBAT_LOG_EVENT_UNFILTERED\" then\n        local destName = select(9, ...)\n        local num = aura_env.deaths[destName]\n        if UnitIsPlayer(destName) then\n            if UnitHealth(destName) == 0 then\n                aura_env.deaths[destName] = num and num+1 or 1\n            end\n            if s[\"\"] then\n                local deaths, timelost = C_ChallengeMode.GetDeathCount()\n                s[\"\"].deathcount = deaths\n                s[\"\"].tooltip = aura_env.tooltip(deaths, timelost)\n                s[\"\"].changed = true\n                return true\n            end\n        end\n    end\n    \nend",
