["init"] = {
["custom"] = "-- seconds to hold down modifier to enable the release button\naura_env.TIMEOUT = aura_env.config.TIMEOUT\n\n-- format the text that explains this behavior\naura_env.INFO_TEXT = \"Hold %s for %0.1f seconds to release.\"\naura_env.INFO_TEXT_SAFE = \"Keep holding and click the button.\"\n\n-- text shown below if someone can get back up and revive the group\naura_env.INFO_TEXT_HAS_RESS = \"|cffFF5555%d |4RESSER:RESSERS; CAN REVIVE EVERYONE!|r\"\naura_env.INFO_TEXT_HAS_RESS_NEARBY = \"|cffFF5555%d |4RESSER:RESSERS; CAN REVIVE YOU!|r\"\n\n-- default auto-release conditions\naura_env.AUTO_RELEASE_IN_MINIMAP_ZONE = {\n    -- { map = 2549, zone = \"The Blessed Boughs\" }, -- Tindral (Amirdrassil)\n    -- { map = 2549, zone = \"Heart of Amirdrassil\" }, -- Fyrakk (Amirdrassil)\n}\n\n-- evaluate if the conditions are met for the current zone and map\nlocal function CheckAutoReleaseConditions(conditions, zone, map)\n    if not conditions or not zone or not map or type(conditions) ~= \"table\" then\n        return\n    end\n    for _, condition in pairs(conditions) do\n        if condition.zone == zone then\n            if not condition.map or condition.map == -1 then\n                return true\n            end\n            if condition.map == map then\n                return true\n            end\n        end\n    end\n    return false\nend\n\n-- evaluate if auto-release is enabled for the current minimap area\nlocal function IsAutoReleasedEnabled()\n    local zone = GetMinimapZoneText()\n    local _, _, _, _, _, _, _, instID = GetInstanceInfo()\n    if zone and instID then\n        if CheckAutoReleaseConditions(aura_env.AUTO_RELEASE_IN_MINIMAP_ZONE, zone, instID) then\n            return true, true\n        end\n        if CheckAutoReleaseConditions(aura_env.config.AUTO_RELEASE_IN_MINIMAP_ZONE, zone, instID) then\n            return true, true\n        end\n    end\n    return aura_env.config.AUTO_RELEASE, false\nend\n\n-- matches the config order of INSTANCE_DIFFICULTIES\nlocal INSTANCE_DIFFICULTIES_MAP = {\n    [1] = 0, -- None (Open World)\n    [2] = 1, -- Dungeon (Normal)\n    [3] = 2, -- Dungeon (Heroic)\n    [4] = 3, -- 10 Player Raid (Normal)\n    [5] = 4, -- 25 Player Raid (Normal)\n    [6] = 5, -- 10 Player Raid (Heroic)\n    [7] = 6, -- 25 Player Raid (Heroic)\n    [8] = 7, -- Legacy Looking For Raid\n    [9] = 8, -- Mythic Keystone\n    [10] = 9, -- 40 Player Raid\n    [11] = 11, -- Scenario (Heroic)\n    [12] = 12, -- Scenario (Normal)\n    [13] = 14, -- Raid (Normal)\n    [14] = 15, -- Raid (Heroic)\n    [15] = 16, -- Raid (Mythic)\n    [16] = 17, -- Looking For Raid\n    [17] = 23, -- Dungeon (Mythic)\n    [18] = 24, -- Dungeon (Timewalking)\n    [19] = 33, -- Raid (Timewalking)\n    [20] = 38, -- Island Expedition (Normal)\n    [21] = 39, -- Island Expedition (Heroic)\n    [22] = 40, -- Island Expedition (Mythic)\n    [23] = 45, -- Island Expedition (PvP)\n    [24] = 147, -- Warfront (Normal)\n    [25] = 149, -- Warfront (Heroic)\n    [26] = 152, -- Visions of N'Zoth\n    [27] = 167, -- Torghast\n    [28] = 168, -- Path of Ascension: Courage\n    [29] = 169, -- Path of Ascension: Loyalty\n    [30] = 170, -- Path of Ascension: Wisdom\n    [31] = 171, -- Path of Ascension: Humility\n    [32] = 205, -- Follower Dungeon\n    [33] = 208, -- Delve\n    [34] = 216, -- Quest Party\n    [35] = 220, -- Story Raid\n}\n\n-- is the difficulty enabled in the config\nlocal function IsDifficultyEnabled(diffID)\n    if not diffID then\n        return\n    end\n    for i, enabled in ipairs(aura_env.config.INSTANCE_DIFFICULTIES) do\n        if enabled then\n            local instDiffID = INSTANCE_DIFFICULTIES_MAP[i]\n            if instDiffID == diffID then\n                return true\n            end\n        end\n    end\n    return false\nend\n\n-- get modifier key\nlocal function IsModifierKeyDown()\n    if aura_env.config.modKey == 1 then\n        return IsShiftKeyDown, \"SHIFT\"\n    elseif aura_env.config.modKey == 3 then\n        return IsAltKeyDown, \"ALT\"\n    end\n    return IsControlKeyDown, \"CTRL\"\nend\n\n-- unit can revive others\nlocal function UnitCanReviveOthers(unit, cr)\n    local _, _, classID = UnitClass(unit)\n    return classID == 2 or classID == 5 or classID == 7 or classID == 10 or classID == 11 or classID == 13 or (cr and classID == 9)\nend\n\n-- any class with self-revive that can revive the group?\nlocal function GetNumRessers()\n    local c, n = 0, 0\n    for unit in WA_IterateGroupMembers() do\n        if UnitIsDeadOrGhost(unit) then\n            if UnitHasIncomingResurrection(unit) and UnitCanReviveOthers(unit, true) then\n                c = c + 1\n            end\n        elseif UnitInRange(unit) and UnitCanReviveOthers(unit) then\n            n = n + 1\n        end\n    end\n    return c, n\nend\n\n-- copied from StaticPopup.lua#L26\nlocal function GetSelfResurrectDialogOptions()\n    local resOptions = GetSortedSelfResurrectOptions()\n    if resOptions then\n        if IsEncounterLimitingResurrections() then\n            return resOptions[1], resOptions[2]\n        else\n            return resOptions[1]\n        end\n    end\nend\n\n-- returns true if we are in a instance where we want to block accidental releasing\nfunction aura_env:IsBlockingEnabled()\n    local _, _, instDiffID = GetInstanceInfo()\n    return IsDifficultyEnabled(instDiffID)\nend\n\n-- returns the popup widget\nfunction aura_env:GetPopup()\n    local _, sp = StaticPopup_Visible(\"DEATH\")\n    return sp\nend\n\n-- returns true if we can release and the button is enabled by default\nfunction aura_env:CanRelease()\n    local sp = self:GetPopup()\n    local allowed = true\n    if sp then\n        if IsFalling() and not IsOutOfBounds() then\n            allowed = false -- wait until corpse lands\n        elseif IsEncounterSuppressingRelease() then\n            allowed = false -- encounter keeps us dead\n        elseif HasNoReleaseAura() then\n            allowed = false -- forced to stay dead\n        else\n            -- self ress options with potential restrictions\n            local option1, option2 = GetSelfResurrectDialogOptions()\n            if option1 and not option1.canUse then\n                allowed = false\n            end\n            if option2 and not option2.canUse then\n                allowed = false\n            end\n        end\n    end\n    return allowed, sp\nend\n\n-- returns true if we aren't restricted and can release\nfunction aura_env:IsReleaseAllowed()\n    local canRelease, sp = self:CanRelease()\n    if canRelease and sp then\n        local releaseAllowed = not aura_env:IsBlockingEnabled()\n        if not releaseAllowed then\n            local _, remain = aura_env:UpdateModifierStatus()\n            if remain then\n                return remain <= 0\n            end\n        end\n        return releaseAllowed\n    end\n    return true\nend\n\n-- called every second in order to keep the button status up-to-date\nfunction aura_env:UpdateButton()\n    local canRelease, sp = self:CanRelease()\n    if not canRelease then\n        return false\n    end\n    -- update button status\n    local releaseAllowed = self:IsReleaseAllowed()\n    -- sp.button1:SetEnabled(releaseAllowed)\n    sp.button1:SetShown(releaseAllowed)\n    -- auto repop if conditions are met\n    local autoRelease, autoReleaseInstant = IsAutoReleasedEnabled()\n    if autoRelease and (autoReleaseInstant or releaseAllowed) and self:IsBlockingEnabled() then\n        RepopMe()\n    end\n    -- append our explanation text and resize the popup widget\n    local text = sp.text:GetText()\n    if text then\n        local _, remain, modName = aura_env:UpdateModifierStatus()\n        local t\n        if remain and remain > 0 then\n            t = format(self.INFO_TEXT, modName, remain)\n        elseif remain then\n            t = self.INFO_TEXT_SAFE\n        else\n            t = format(self.INFO_TEXT, modName, aura_env.TIMEOUT)\n        end\n        local c, n = GetNumRessers()\n        if n > 0 then\n            t = format(\"%s\\n%s\", aura_env.INFO_TEXT_HAS_RESS_NEARBY:format(n), t)\n        elseif c > 0 then\n            t = format(\"%s\\n%s\", aura_env.INFO_TEXT_HAS_RESS:format(c), t)\n        end\n        sp.text:SetText(t)\n        StaticPopup_Resize(sp, sp.which)\n    end\n    return true\nend\n\n-- called to update modifier status, returns also seconds elapsed\nfunction aura_env:UpdateModifierStatus()\n    local isModifierKeyDown, modifierKeyName = IsModifierKeyDown()\n    if aura_env.TIMEOUT <= 0 then\n        return 0, 0, modifierKeyName\n    elseif isModifierKeyDown() then\n        if not aura_env.keyPressed then\n            aura_env.keyPressed = GetTime() + aura_env.TIMEOUT\n        end\n    else\n        aura_env.keyPressed = nil\n    end\n    local elapsed = aura_env.keyPressed\n    if elapsed then\n        return elapsed, elapsed - GetTime(), modifierKeyName\n    end\n    return elapsed, nil, modifierKeyName\nend\n\n-- creates shake animation group\nlocal function CreateShake(sp)\n    local a = sp:CreateAnimationGroup()\n    a:SetLooping(\"REPEAT\")\n    local t = a:CreateAnimation(\"Translation\")\n    t:SetDuration(0)\n    t:SetOffset(0, 0)\n    a.t = t\n    a:SetScript(\"OnStop\", function() t:SetOffset(0, 0) end)\n    sp:HookScript(\"OnHide\", function() a:Stop() end)\n    return a\nend\n\n-- starts shaking and updates the animation each call\nlocal function StartShaking(sp)\n    local a = sp.DontReleaseAnimationGroup\n    if not a then\n        a = CreateShake(sp)\n        sp.DontReleaseAnimationGroup = a\n    end\n    if not a:IsPlaying() then\n        a:Play()\n    end\n    -- get distance from cursor to button\n    local x1, y1 = sp.button1:GetCenter()\n    local scale, x2, y2 = UIParent:GetEffectiveScale(), GetCursorPosition()\n    x2, y2 = x2/scale, y2/scale\n    local dist = sqrt((x2-x1)^2+(y2-y1)^2)\n    -- create a linear shake that intensifies up to 100 pixels the closer we get to the button\n    local t = min(100, max(1, 1000/(dist+1)))\n    a.t:SetOffset(random(-t, t), random(-t, t))\nend\n\n-- stops shaking\nlocal function StopShaking(sp)\n    local a = sp.DontReleaseAnimationGroup\n    if not a then\n        return\n    end\n    if a:IsPlaying() then\n        a:Stop()\n    end\nend\n\n-- update shake effect on the popup dialog\nfunction aura_env:UpdateShake()\n    local canRelease, sp = self:CanRelease()\n    if not sp then\n        return\n    end\n    if not aura_env.config.APE then\n        StopShaking(sp)\n    elseif canRelease and self:IsReleaseAllowed() then\n        StopShaking(sp)\n    else\n        StartShaking(sp)\n    end\nend",
