["init"] = {
["custom"] = "aura_env.debugmode = aura_env.config['debug']\naura_env.version = WeakAuras.GetData(aura_env.id).version\naura_env.versionCheck = 0\naura_env.maxVersion = 0\naura_env.versionPersonal = aura_env.config['versionPersonal']\naura_env.showEncounter = aura_env.config['encounter']\naura_env.selectionColour = aura_env.config['selection']\naura_env.answerColour = aura_env.config['answer']\naura_env.potColour = aura_env.config['potentialSol']\naura_env.announceClicked = aura_env.config['announceClicked']\naura_env.announceSolution = aura_env.config['announceSolution']\naura_env.lotusFlower = aura_env.config['lotusFlower']\naura_env.announceStyle = aura_env.config['announceStyle']\naura_env.announce = aura_env.config['announcer']\naura_env.matchFound = false\naura_env.noCommunication = aura_env.config['noCommunication']\naura_env.useEng = aura_env.config['useEng']\naura_env.eLang = aura_env.config['eLang']\naura_env.lastSolvePH = \"\"\naura_env.announcer = false\naura_env.encounter = false\naura_env.edgeCase = false\naura_env.fiveFour = aura_env.config['fiveFour']\naura_env.specializationName = {\n    [250] = \"TANK\",\n    [581] = \"TANK\",\n    [104] = \"TANK\",\n    [268] = \"TANK\",\n    [66] = \"TANK\",\n    [73] = \"TANK\",\n    [105] = \"HEALER\", \n    [270] = \"HEALER\", \n    [65] = \"HEALER\", \n    [256] = \"HEALER\", \n    [257] = \"HEALER\", \n    [264] = \"HEALER\", \n}\nfunction aura_env.announcement(which,i,count,mark,experiment,cout,sout,fout)\n    aura_env.announcer = false\n    local lead = UnitIsGroupLeader(\"player\")\n    aura_env.playerInfo = aura_env.specializationName[GetSpecializationInfo(GetSpecialization())]\n    if lead and aura_env.announce[1] == true then\n        aura_env.announcer = true\n    elseif aura_env.playerInfo == \"TANK\" and aura_env.announce[2] == true then\n        aura_env.announcer = true\n    elseif aura_env.playerInfo == \"HEALER\" and aura_env.announce[3] == true then\n        aura_env.announcer = true\n    elsei