e..\")|r\"\n                else\n                    text4 = \"(\"..aura_env.state.mcpullCompare..\")\"\n                end\n            end\n        end\n        \n        \n        if aura_env.remaining then\n            if aura_env.totalC then\n                text3 = aura_env.state.mcCompare..\"/\"..aura_env.state.totalMC\n            else\n                text3 = aura_env.state.mcCompare\n            end\n        else\n            if aura_env.state.totalMC > 1 then\n                if aura_env.totalC then\n                    text3 = aura_env.state.currentMC..\"/\"..aura_env.state.totalMC\n                else\n                    text3 = aura_env.state.currentMC\n                end\n            end\n        end            \n        text1 = ((aura_env.display == 1 or aura_env.display == 2) and text1) or \"\"\n        text2 = ((aura_env.display == 1 or aura_env.display == 2) and text2) or \"\"\n        text3 = ((aura_env.display == 1 or aura_env.display == 3) and text3) or \"\"\n        text4 = ((aura_env.display == 1 or aura_env.display == 3) and text4) or \"\"\n        return text1, text2, text3, text4\n    end\n    if WeakAuras.IsOptionsOpen() then\n        if not C_ChallengeMode.GetActiveChallengeMapID() then\n            text1, text2, text3, text4 = aura_env.getoptionstext()\n        end\n        return text1, text2, text3, text4\n    end\nend\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n",
