 }\n            },\n            changed = true,\n        }\n    elseif e == \"CHALLENGE_MODE_COMPLETED\" then\n        \n        local finish = select(3, C_ChallengeMode.GetCompletionInfo())\n        local cmap = aura_env.cmap\n        local level = aura_env.level \n        if cmap and level then\n            if not aura_env.saved then aura_env.saved = {} end\n            if not aura_env.saved[cmap] then aura_env.saved[cmap] = {} end\n            if not aura_env.saved[cmap][level] then aura_env.saved[cmap][level] = {} end\n            \n            if not aura_env.saved[cmap][level][\"finish\"] or finish < aura_env.saved[cmap][level][\"finish\"] then\n                aura_env.saved[cmap][level][\"finish\"] = finish\n                aura_env.saved[cmap][level][0] = aura_env.finishtime\n            end\n        end\n        s[\"\"].value = 100\n        s[\"\"].total = 100\n        s[\"\"].changed = true\n        return true\n    elseif e == \"SCENARIO_POI_UPDATE\" or e == \"CHALLENGE_MODE_START\" or e == \"SCENARIO_CRITERIA_UPDATE\" or e == \"STATUS\" then\n        local time = select(3, C_ChallengeMode.GetCompletionInfo())\n        if e == \"CHALLENGE_MODE_START\" then\n            \n            aura_env.finish = false\n            aura_env.done = false\n            aura_env.update = true\n            aura_env.cmap = nil\n            local level, affixes = C_ChallengeMode.GetActiveKeystoneInfo()\n            aura_env.cmap = C_ChallengeMode.GetActiveChallengeMapID()\n            aura_env.level = level\n        end\n        if time == 0 and not aura_env.cmap then\n            local level, affixes = C_ChallengeMode.GetActiveKeystoneInfo()\n            aura_env.cmap = C_ChallengeMode.GetActiveChallengeMapID()\n            aura_env.level = level\n        end\n        local progress, mobCount, currentMC, totalMC = aura_env.GetProgress()\n        totalMC = totalMC or 0\n        aura_env.total = totalMC\n        if time ~= 0 then \n            progress = 100 \n            currentMC = totalMC\n            mobCount = currentMC..\"/\"..totalMC\n        end\n        if not aura_env.done then\n            progress = progress or 0\n        end\n        if progress then\n            currentMC = currentMC or totalMC\n            mobCount = mobCount or 0\n            local total = 100\n            \n            aura_env.total = aura_env.total or 0\n            \n            if progress >= 100 and not aura_env.done then\n                \n                local max = select(3, C_Scenario.GetStepInfo())\n                local defeat = C_ScenarioInfo.GetCriteriaInfo(max) and C_ScenarioInfo.GetCriteriaInfo(max).elapsed or 0\n                local cur = select(2, GetWorldElapsedTime(1)) - defeat\n                local cmap = aura_env.cmap\n                local level = aura_env.level \n                local haspb = aura_env.config.splits and aura_env.saved and aura_env.saved[cmap] and aura_env.saved[cmap][level] and aura_env.saved[cmap][level][0]\n                if haspb then                \n                    local pb = aura_env.saved[cmap][level][0]\n                    if cur == pb then\n                        aura_env.finish = aura_env.formattime(cur, aura_env.color)..\" |cFFFFEA00 +-0\"\n                    else\n                        local prefix = cur <= pb and \" -\" or \" +\"\n                        local color = cur <= pb and \"|cFF00FF00\" or \"|cFFFF0000\"\n                        local diff = cur-pb\n                        if diff < 0 then diff = diff*-1 end\n                        aura_env.finish = \"[\"..aura_env.formattime(cur, aura_env.color)..\"]\"..color..prefix..aura_env.formattime(diff, color)\n                    end\n                else\n                    aura_env.finish = \"[\"..aura_env.formattime(cur, aura_env.color)..\"]\"\n                end\n                aura_env.finishtime = cur\n            end\n            if (e == \"STATUS\" or e == \"SCENARIO_CRITERIA_UPDATE\" or e == \"SCENARIO_POI_UPDATE\") and prog