["init"] = {
["custom"] = "aura_env.keyslot = aura_env.config[\"KeySlot\"]\n\naura_env.keyID = {\n    [138019] = true, -- Legion\n    [158923] = true, -- Battle for Azeroth\n    [180653] = true, -- Shadowlands\n    [186159] = true, -- Dragonflight\n    [187786] = true, -- Timewalking\n    [151086] = true, -- Tournament Realm?\n    \n}\naura_env.gossips = {}\nfor _, v in pairs(aura_env.config.gossip) do\n    aura_env.gossips[v.npcid] = {enabled = v.enabled, number = v.gossipnum}\nend\n\n\n\n\naura_env.popup = function()\n    for index = 1, STATICPOPUP_NUMDIALOGS do\n        local frame = _G[\"StaticPopup\"..index]\n        if frame and frame:IsShown() then\n            return true\n        end\n    end\n    return false\nend\n\nlocal function decRound(num, idp)\n    local mult = 10^(idp or 0)\n    return math.floor(num * mult + 0.5) / mult\nend\n\naura_env.kaliels = C_AddOns.IsAddOnLoaded(\"!KalielsTracker\")\naura_env.frame = aura_env.kaliels and _G[\"!KalielsTrackerFrame\"] or ObjectiveTrackerFrame\nif aura_env.config.hideobj then\n    if not aura_env.region.hideobj then\n        aura_env.region.hideobj = true\n        local frame = aura_env.frame\n        hooksecurefunc(frame, \"Show\", function() \n                if IsInInstance() and C_ChallengeMode.IsChallengeModeActive() then frame:Hide() end\n        end)\n    end\nend\n\n\nlocal tooltip = aura_env.config.Tooltip\n\nlocal aura_env = aura_env\nif tooltip ~= 1 and not aura_env.region.tooltipfunc then\n    aura_env.region.tooltipfunc = true\n    aura_env.last = {}\n    local function OnTooltipSetUnit()\n        if select(3, GetInstanceInfo()) == 8 then\n            local GUID = UnitGUID(\"mouseover\")\n            if GUID and MDT then\n                local npcID = select(6, strsplit(\"-\", GUID))\n                local count\n                local max\n                count, max = MDT:GetEnemyForces(tonumber(npcID))\n                if count and max and count ~= 0 and max ~= 0 then\n                    local percent = decRound((count/max)*100, 2)..\"%\"\n                    \n                    local string = (tooltip == 4 and count..\" (\"..percent..\")\") or (tooltip  == 3 and percent) or (tooltip  == 2 and count)\n                    local now = GetTime()\n                    if string and (not aura_env.last[GUID] or now >= aura_env.last[GUID]+0.1) then\n                        aura_env.last[GUID] = now\n                        GameTooltip:AppendText(\" - \"..string)\n                    end\n                end\n            end\n        end\n    end\n    TooltipDataProcessor.AddTooltipPostCall(Enum.TooltipDataType.Unit, OnTooltipSetUnit)\nend\n\n\n",
["do_custom"] = true,
},
