["custom"] = "aura_env.mapID = aura_env.mapID or 0\naura_env.timer = aura_env.timer or 0\naura_env.timelimit = aura_env.timelimit or 0\naura_env.two = aura_env.two or 0\naura_env.three = aura_env.three or 0\naura_env.decimalsF = aura_env.config[\"DecimalsF\"]\n\n\nlocal c = aura_env.config.color\nlocal col = {}\n\nfor i=1, 4 do\n    if i == 4 then \n        col[i] = string.format(\"%x\", c[i] *255)\n    else\n        col[i] = string.format(\"%x\", c[i] *255)\n    end\n    if string.len(col[i]) == 1 then\n        col[i] = \"0\"..col[i]\n    end\nend\n\naura_env.color = \"|c\"..col[4]..col[1]..col[2]..col[3]\n\naura_env.formattime = function(time, round)\n    if time then\n        local timeMin = math.floor(time / 60)\n        local timeSec = round and Round(time - (timeMin*60)) or math.floor(time - (timeMin*60))\n        local timeHour = 0\n        if timeMin >= 60 then\n            timeHour = math.floor(time / 3600)\n            timeMin = timeMin - (timeHour * 60)\n        end\n        if timeHour > 0 and timeHour < 10 then\n            timeHour = (\"0%d\"):format(timeHour)\n        end\n        if timeMin < 10 then\n            timeMin = (\"0%d\"):format(timeMin)\n        end\n        if timeSec < 10 then\n            timeSec = (\"0%d\"):format(timeSec)\n        end        \n        if timeHour ~= 0 then\n            return (\"%s:%s:%s\"):format(timeHour, timeMin, timeSec)\n        else\n            return (\"%s:%s\"):format(timeMin, timeSec)\n        end\n    end\nend",
