["displayText_format_p_time_dynamic_threshold"] = 60,
["customText"] = "function()\n    function c(nameSubstring)\n        -- Ensure nameSubstring is a string, otherwise return the default\n        if type(nameSubstring) ~= \"string\" then\n            return \"|c00FF2949\"..nameSubstring..\"|r\"\n        end\n        \n        -- Check if we are in a raid or party group\n        if IsInGroup() then\n            local groupSize = GetNumGroupMembers()\n            \n            -- Iterate through the entire group (raid or party)\n            for i = 1, groupSize do\n                local name, _, _, _, class, _, _, _, _, _, _, _ = GetRaidRosterInfo(i)\n                \n                -- Check if the player's name contains the substring (case insensitive)\n                if name and name:lower():find(nameSubstring:lower()) then\n                    -- Get the class color using GetClassColor\n                    local _,_,_,classColor = GetClassColor(class:upper():gsub(\"%s\", \"\"))\n                    if classColor then\n                        return \"|c\"..classColor..nameSubstring..\"|r\"\n                    else\n                        -- If classColor is invalid, fallback to default color\n                        return \"|c00FF2949\"..nameSubstring..\"|r\"\n                    end\n                end\n            end\n        end\n        \n        -- If no player is found or unit is not in raid, return default color (red) with the name\n        return \"|c00FF2949\"..nameSubstring..\"|r\"\n    end\n    \n    local poppers = {}\n    \n    -- Loop through the groups (1 to 4)\n    for gcount = 1, 4 do\n        -- Dynamically build the group key and insert values into the poppers table\n        for p = 1, 4 do\n            local groupKey = \"twws2_mine_pop_g\" .. gcount\n            local popValue = aura_env.config[groupKey][\"p\" .. p]\n            table.insert(poppers, popValue)\n        end\n    end\n    \n    local sp = \"|c00FFFFFF > |r\"\n    local s1 = \"|c003FC7EBGroup 1: |r\" .. c(poppers[1]) .. sp .. c(poppers[2]) .. sp .. c(poppers[3]) .. sp .. c(poppers[4]) .. \"\\n\"\n    local s2 = \"|c0000FF98Group 2: |r\" .. c(poppers[5]) .. sp .. c(poppers[6]) .. sp .. c(poppers[7]) .. sp .. c(poppers[8]) .. \"\\n\"\n    local s3 = \"|c00FF7C0AGroup 3: |r\" .. c(poppers[9]) .. sp .. c(poppers[10]) .. sp .. c(poppers[11]) .. sp .. c(poppers[12]) .. \"\\n\"\n    local s4 = \"|c00F48CBAGroup 4: |r\" .. c(poppers[13]) .. sp .. c(poppers[14]) .. sp .. c(poppers[15]) .. sp .. c(poppers[16]) .. \"\\n\"\n    \n    return s1..s2..s3..s4\nend",
