["event"] = "Health",
["unit"] = "player",
["custom_type"] = "event",
["custom"] = "function()\n    -- Author Notes: \n    -- aura_env.config[\"howhidetasks\"],   1 = Hide From View, 2 = Mark As Complete\n    \n    --print(\"E: \" .. event)\n    \n    local cHeader = \"|cff\"\n    local gColor = cHeader .. \"34ff80\"\n    local rColor = cHeader.. \"ff3932\"\n    \n    local m1Color = cHeader .. \"b42222\"\n    local m2Color = cHeader .. \"577000\"\n    local m3Color = cHeader .. \"007d68\"\n    local m4Color = cHeader .. \"0088a8\"\n    local m5Color = cHeader .. \"db08fd\"\n    \n    local subHeader = cHeader .. \"00DDFF\"\n    \n    local endLine = \"|r\\n\"\n    local howhide = aura_env.config[\"howhidetasks\"]\n    \n    local raidTrackHeader = subHeader .. \"[## Raiding ##]\" .. endLine\n    local mythicTrackHeader = subHeader .. \"[## Mythic ##]\" .. endLine\n    local mythicPlusTrackHeader = subHeader .. \"[## Mythic+ ##]\" .. endLine\n    \n    local resCrystalGoal = 8000 -- 2815\n    local valorStoneGoal = 2000 -- 3008\n    local carvedCrest = 90 -- 2915\n    local weatheredCrest = 90 -- 2914\n    local runedCrest = 90  -- 2916\n    local cofferKey = 90 -- 3028\n    \n    local currencyTrack = {'trackresonancecrystals','trackvalorstone','trackcarvedcrest','trackweathercrests','trackrunedcrests','trackgildedcrest','trackcofferkeys','trackkej'}\n    local currenciesName = {'Resonance Crystals','Valorstones','Carved Crests','Weathered Crests','Runed Crests','Gilded Crests','Coffer Keys','Kej'}\n    local currenciesId = {2815,3008,2915,2914,2916,2917,3028,3056}\n    local currenciesGoals = {1,2000,90,90,90,90,90,1000}\n    \n    local campQuestID = 83503\n    \n    local renownName = {'Council of Dornogal','Hallowfall Arathi','The Assembly of the Deep','The Severed Threads'}\n    local renownId = {2590,2570,2594,2600}\n    local renownGoals = 25\n    \n    avgItemLevel, avgItemLevelEquipped, avgItemLevelPvp = GetAverageItemLevel()\n    local e = ((\"%.1f\"):format(avgItemLevelEquipped))\n    local avg, equipped_avg = GetAverageItemLevel()\n    local goalilvl = 616\n    \n    -- Head, Shoulder, Chest, Legs, Hands\n    local tierSlots = {1,3,5,7,10}\n    local tierItemIDs = {212002,212000,212005,212003,212001,212065,212063,212068,212066,212064,212056,212054,212059,212057,212055,212029,212027,212032,212030,212028,212020,212018,212023,212021,212019,212092,212090,212095,212093,212091,212047,212045,212050,212048,212046,211993,211991,211996,211994,211992,212083,212081,212086,212084,212082,212038,212036,212041,212039,212037,212011,212009,212014,212012,212010,212074,212072,212077,212075,212073,211984,211982,211987,211985,211983}    \n    \n    local wqName = {'The Theater Troupe','Awakening the Machine','Spreading the Light'}\n    local wqId = {83240,83333,76586}\n    \n    local pveTrack = {'trackmythicdungeon','trackmythicplus','trackraids','trackworldboss'}\n    local seasonMythicsName = {\"Ara-Kara\",\"Threads\", \"Dawnbreaker\", \"Stonevault\", \"Mists\", \"Necrotic Wake\", \"Boralus\", \"Grim Batol\"}\n    local seasonMythicsId = {2660,2669,2662,2652,2290,2286,1822,670}\n    local worldBoss = {}\n    \n    -- 2657:Nerub-ar Palace, \n    local raidId = {2549,2657}\n    local raidName = {\"Amirdrassil\",\"Nerub-ar Palace\"}\n    \n    local function tabContains(tab, val)\n        for i=1, getn(tab) do\n            if val == tab[i] then\n                return true\n            end        \n        end\n        \n        return false\n    end    \n    \n    finalString = \"------------Soups Weekly Goals------------\" .. endLine\n    \n    for i = 1, getn(currencyTrack) do\n        if aura_env.config[currencyTrack[i]] then\n            local c = C_CurrencyInfo.GetCurrencyInfo(currenciesId[i])\n            if c.discovered then\n                local q = c.quantity\n                \n                if c.useTotalEarnedForMaxQty then\n                    if c.totalEarned >= c.maxQuantity then\n                        if howhide == 1 then\n                            -- We return nothing.\n                        else\n                            finalString = finalString .. gColor .. currenciesName[i] .. \": Season: \" .. q .. \" [\" .. c.totalEarned ..\"/\".. c.maxQuantity .. \"]\" .. endLine\n                        end\n                    else\n                        finalString = finalString .. rColor .. currenciesName[i] .. \": Season: \" .. q .. \" [\" .. c.totalEarned ..\"/\".. c.maxQuantity .. \"]\" .. endLine\n                    end\n                else\n                    if q >= currenciesGoals[i] then\n                        if howhide == 1 then\n                            -- We return nothing.\n                        else\n                            finalString = finalString .. gColor .. currenciesName[i] .. \": \"..q.. \"/\"..currenciesGoals[i].. endLine\n                        end\n                    else\n                        finalString = finalString .. rColor .. currenciesName[i] .. \": \"..q.. \"/\"..currenciesGoals[i].. endLine\n                    end\n                end\n            end\n        end\n    end\n    \n    if aura_env.config[\"trackrenown\"] then\n        finalString = finalString .. \"--------------------Renown--------------------\" .. endLine\n    end\n    \n    local twwCampaignAchieveID = 20597\n    _,_,_,twwCampaignCompleted = GetAchievementInfo(twwCampaignAchieveID)\n    \n    if twwCampaignCompleted then\n        if howhide == 1 then\n            -- We return nothing.\n        else\n            finalString = finalString .. gColor .. \"TWW Campaign\" .. \": Complete!\" .. endLine\n        end\n    else\n        finalString = finalString .. rColor .. \"TWW Campaign\" .. \": Incomplete!\" .. endLine\n    end\n    \n    for i = 1, getn(renownId) do\n        if aura_env.config[\"trackrenown\"] then\n            local c = C_MajorFactions.GetMajorFactionRenownInfo(renownId[i])\n            local rl = c.renownLevel\n            \n            local t = c.renownLevelThreshold\n            local e= c.renownReputationEarned\n            local percent = \" [\" .. ((e/t) * 100) .. '%]'\n            \n            if rl >= renownGoals then\n                if howhide == 1 then\n                    -- We return nothing.\n                else\n                    finalString = finalString .. gColor .. renownName[i] .. \": \"..rl.. \"/\"..renownGoals.. percent .. endLine\n                end\n            else\n                finalString = finalString .. rColor .. renownName[i] .. \": \"..rl.. \"/\"..renownGoals.. percent .. endLine\n            end\n        end\n    end\n    \n    if aura_env.config[\"trackitemlevel\"] then\n        finalString = finalString .. \"-------------------Item Level-------------------\" .. endLine\n    end\n    \n    if aura_env.config[\"trackitemlevel\"] then\n        if equipped_avg >= goalilvl then\n            if howhide == 1 then\n                -- We return nothing.\n            else\n                finalString = finalString .. gColor .. \"Item Level: \"..e.. \"/\"..goalilvl.. endLine\n            end\n        else\n            finalString = finalString .. rColor .. \"Item Level: \"..e.. \"/\"..goalilvl.. endLine\n        end\n    end\n    \n    tierSetNum = 0\n    for i = 1, getn(tierSlots) do\n        itemId = GetInventoryItemID(\"player\", tierSlots[i]);\n        itemName, itemLink, itemRarity, itemLevel, itemMinLevel, itemType,itemSubType, itemStackCount, itemEquipLoc, itemTexture, itemSellPrice = GetItemInfo(itemId)\n        \n        if(tabContains(tierItemIDs, itemId)) then\n            tierSetNum = tierSetNum + 1\n        end\n    end\n    \n    if (tierSetNum >= 4) then\n        finalString = finalString .. gColor .. \"Tier Set: \"..tierSetNum.. \"/5\" .. endLine\n    else\n        finalString = finalString .. rColor .. \"Tier Set: \"..tierSetNum.. \"/5\" .. endLine\n    end    \n    \n    if aura_env.config[\"trackweeklyquests\"] then\n        finalString = finalString .. \"----------------Weekly Quests----------------\" .. endLine\n    end\n    \n    for i = 1, getn(wqId) do\n        if aura_env.config[\"trackweeklyquests\"] then\n            \n            local hour,minute = GetGameTime()\n            if (wqId[i] == 83240) then\n                local timeUntil = (60 - minute)\n                finalString = finalString .. \"[Next: \" .. timeUntil .. \"m] \"\n            end\n            \n            if C_QuestLog.IsQuestFlaggedCompleted(wqId[i]) then\n                if howhide == 1 then\n                    -- We return nothing.\n                else\n                    finalString = finalString .. gColor .. wqName[i] .. \": Complete!\" .. endLine\n                end\n            else\n                finalString = finalString .. rColor .. wqName[i] .. \": Incomplete!\" .. endLine\n            end\n        end\n    end    \n    \n    pvetracking = false\n    \n    for i = 1, getn(pveTrack) do\n        if aura_env.config[pveTrack[i]] then\n            pvetracking = true\n        end\n    end\n    \n    \n    if pvetracking then\n        finalString = finalString .. \"----------------PVE Tracking----------------\" .. endLine\n    end\n    \n    mythicdungeonInfo = \"\"\n    mythicplusInfo = \"\"\n    raidInfo = \"\"\n    \n    \n    -- TRACKING MYTHIC DUNGEONS (Mythic Zero)\n    if aura_env.config[\"trackmythicdungeon\"] then\n        if (equipped_avg <= 605) then            \n            local function mzeroProg(id)\n                local nI = GetNumSavedInstances()\n                for i=1, nI do\n                    n,_,r,dId,_,_,_,_,_,_,ne,ep,_,iId = GetSavedInstanceInfo(i)\n                    \n                    if (id == iId) then\n                        if (r > 0) then\n                            return n, ne, ep\n                        end                    \n                    end\n                end\n            end\n            \n            \n            for i=1, getn(seasonMythicsId) do\n                if (mzeroProg(seasonMythicsId[i]) == nil) then\n                    mythicdungeonInfo = mythicdungeonInfo .. rColor .. \"[M] \" .. seasonMythicsName[i] .. \": Incomplete!\" .. endLine\n                else\n                    n, ne, ep = mzeroProg(seasonMythicsId[i])\n                    \n                    if (ne == ep) then\n                        mythicdungeonInfo = mythicdungeonInfo .. gColor .. \"[M] \" .. seasonMythicsName[i] .. \": \" .. ep .. \"/\" .. ne .. endLine\n                    else\n                        if seasonMythicsId[i] == 1822 then\n                            mythicdungeonInfo = mythicdungeonInfo .. gColor .. \"[M] \" .. seasonMythicsName[i] .. \": \" .. ep .. \"/\" .. ne .. endLine\n                        else\n                            mythicdungeonInfo = mythicdungeonInfo .. rColor .. \"[M] \" .. seasonMythicsName[i] .. \": \" .. ep .. \"/\" .. ne .. endLine\n                        end\n                    end\n                end\n            end\n        end\n    end\n    \n    -- TRACKING MYTHIC PLUS\n    if aura_env.config[\"trackmythicplus\"] then\n        ratingSummary = C_PlayerInfo.GetPlayerMythicPlusRatingSummary(\"player\")\n        mScore = ratingSummary['currentSeasonScore']\n        \n        if (mScore <= 1000) then\n            mythicplusInfo = m1Color .. \"[M+] Score: \" .. mScore .. endLine\n        end\n        \n        if (mScore <= 1001 and mScore >= 0) then\n            mythicplusInfo = m2Color .. \"[M+] Score: \" .. mScore .. endLine\n        end\n        \n        if (mScore <= 1501 and mScore >= 0) then\n            mythicplusInfo = m3Color .. \"[M+] Score: \" .. mScore .. endLine\n        end\n        \n        if (mScore <= 2001 and mScore >= 0) then\n            mythicplusInfo = m4Color .. \"[M+] Score: \" .. mScore .. endLine\n        end\n        \n        if (mScore >= 2501) then\n            mythicplusInfo = m5Color .. \"[M+] Score: \" .. mScore .. endLine\n        end\n    end\n    \n    -- TRACKING RAIDS\n    if aura_env.config[\"trackraids\"] then\n        local numSavedInstances = GetNumSavedInstances()\n        \n        local raidData = {}\n        x = 0\n        \n        local amirdrassil = {}        \n        local nerub = {}\n        \n        for i=1, numSavedInstances do\n            name,_,reset,difficultyId,_,_,_,_,_,_,numEncounters,encounterProgress,_,instanceId = GetSavedInstanceInfo(i)\n            if reset>0 then                \n                if tabContains(raidId,instanceId) then                    \n                    if instanceId == 2549 then\n                        \n                        amirdrassil.Name = name\n                        amirdrassil.NE = numEncounters\n                        \n                        if difficultyId == 17 then\n                            amirdrassil.L = encounterProgress\n                        end\n                        \n                        if difficultyId == 14 then\n                            amirdrassil.N = encounterProgress\n                        end\n                        \n                        if difficultyId == 15 then\n                            amirdrassil.H = encounterProgress\n                        end\n                        \n                        if difficultyId == 16 then\n                            amirdrassil.M = encounterProgress\n                        end\n                    end\n                    \n                    if instanceId == 2657 then\n                        \n                        nerub.Name = name\n                        nerub.NE = numEncounters\n                        \n                        if difficultyId == 17 then\n                            nerub.L = encounterProgress                            \n                        end\n                        \n                        if difficultyId == 14 then\n                            nerub.N = encounterProgress\n                        end\n                        \n                        if difficultyId == 15 then                        \n                            nerub.H = encounterProgress\n                        end\n                        \n                        if difficultyId == 16 then\n                            nerub.M = encounterProgress\n                        end                       \n                    end\n                end\n            end\n        end\n        \n        for i=1, #raidId do\n            if raidId[i] == 2549 then\n                raidData[x] = amirdrassil\n            end\n            \n            if raidId[i] == 2657 then\n                raidData[x] = nerub\n            end            \n            \n            x = x + 1\n        end\n        \n        tempData = \"\"\n        for i=0, #raidData do\n            if(raidData[i]['NE'] ~= nil) then                \n                if(raidData[i]['Name'] ~= nil) then\n                    if aura_env.config[\"trackshortraidnames\"] then\n                        tempData = tempData .. \"|cFFC8CC92[|r |cFF06A77D\" .. raidData[i]['Name']:sub(1,5) .. \"|r:  \"\n                    else\n                        tempData = tempData .. \"|cFFC8CC92[|r |cFF06A77D\" .. raidData[i]['Name'] .. \"|r:  \"\n                    end\n                end\n                \n                if(raidData[i]['L'] == nil) then\n                    tempData = tempData .. \"|cFFFC814AL:|r \" .. rColor .. \"0/\" .. raidData[i]['NE'] .. \"|r \"\n                else\n                    if(raidData[i]['L'] == raidData[i]['NE']) then\n                        tempData = tempData .. \"|cFFFC814AL:|r \" .. gColor .. raidData[i]['L'] .. \"/\" .. raidData[i]['NE'] .. \"|r \"\n                    else\n                        tempData = tempData .. \"|cFFFC814AL:|r \" .. rColor .. raidData[i]['L'] .. \"/\" .. raidData[i]['NE'] .. \"|r \"\n                    end\n                end\n                \n                if(raidData[i]['N'] == nil) then\n                    tempData = tempData .. \"|cFF59F8E8N:|r \" .. rColor .. \"0/\" .. raidData[i]['NE'] .. \"|r \"\n                else\n                    if(raidData[i]['N'] == raidData[i]['NE']) then\n                        tempData = tempData .. \"|cFF59F8E8N:|r \" .. gColor .. raidData[i]['N'] .. \"/\" .. raidData[i]['NE'] .. \"|r \"\n                    else\n                        tempData = tempData .. \"|cFF59F8E8N:|r \" .. rColor .. raidData[i]['N'] .. \"/\" .. raidData[i]['NE'] .. \"|r \"\n                    end\n                end\n                \n                \n                if(raidData[i]['H'] == nil) then\n                    tempData = tempData .. \"|cFF20A4F3H:|r \" .. rColor .. \"0/\" .. raidData[i]['NE'] .. \"|r \"\n                else\n                    if(raidData[i]['H'] == raidData[i]['NE']) then\n                        tempData = tempData .. \"|cFF20A4F3H:|r \" .. gColor .. raidData[i]['H'] .. \"/\" .. raidData[i]['NE'] .. \"|r \"\n                    else\n                        tempData = tempData .. \"|cFF20A4F3H:|r \" .. rColor .. raidData[i]['H'] .. \"/\" .. raidData[i]['NE'] .. \"|r \"\n                    end\n                end\n                \n                if(raidData[i]['M'] == nil) then\n                    tempData = tempData .. \"|cFFA06CD5M:|r \" .. rColor .. \"0/\" .. raidData[i]['NE'] .. \"|r |cFFC8CC92]\" .. endLine\n                else\n                    if(raidData[i]['M'] == raidData[i]['NE']) then\n                        tempData = tempData .. \"|cFFA06CD5M:|r \" .. gColor .. raidData[i]['M'] .. \"/\" .. raidData[i]['NE'] .. \"|r |cFFC8CC92]\" .. endLine\n                    else\n                        tempData = tempData .. \"|cFFA06CD5M:|r \" .. rColor .. raidData[i]['M'] .. \"/\" .. raidData[i]['NE'] .. \"|r |cFFC8CC92]\" .. endLine\n                    end\n                end\n            end\n        end\n        raidInfo = tempData\n    end\n    \n    finalString = finalString .. mythicTrackHeader .. mythicdungeonInfo .. mythicPlusTrackHeader .. mythicplusInfo .. raidTrackHeader .. 