["custom"] = "function(allstates, event, ...)\n    \n    if not allstates[\"\"] then \n        local currentP, maxP = UnitPower(\"player\",aura_env.power.PowerTypes, aura_env.power.unmodified) or 0, UnitPowerMax(\"player\",aura_env.power.PowerTypes, aura_env.power.unmodified) or 1\n        allstates[\"\"] = {\n            show = true,\n            progressType = \"static\",\n            value = currentP,\n            total = maxP,\n            value_original = currentP,\n            total_original = maxP,\n            short = AbbreviateNumbers(currentP),\n            casting = false,\n            change = 0,\n            changeText = 0,\n            type = aura_env.power.PowerTypes,\n            additionalProgress = {{},{},},\n            costInfo = {}\n        }\n    end\n    local s = allstates[\"\"]\n    \n    if event == \"UNIT_DISPLAYPOWER\" or event == \"LOAD\" or event == \"OPTIONS\" or event == \"PLAYER_SPECIALIZATION_CHANGED\" then\n        \n        s.type = aura_env.power.PowerTypes\n        s.value = UnitPower(\"player\", s.type, aura_env.power.unmodified)\n        s.total = UnitPowerMax(\"player\", s.type, aura_env.power.unmodified)\n        s.value_original, s.total_original = s.value, s.total\n        s.short = AbbreviateNumbers(s.value)\n        s.casting = false\n        if s.costInfo.type and s.costInfo.type ~= s.type then\n            s.additionalProgress = {{},{},}\n            s.costInfo = {}\n            s.change = 0\n        end\n        \n    elseif event == \"UNIT_POWER_FREQUENT\" then\n        s.value = UnitPower(\"player\", s.type, aura_env.power.unmodified)\n        s.value_original = s.value\n        s.short = AbbreviateNumbers(s.value)\n        \n    elseif event == \"UNIT_POWER_UPDATE\" then\n        s.total = UnitPowerMax(\"player\", s.type, aura_env.power.unmodified)\n        s.total_original = s.total\n        \n    elseif event == \"UNIT_MAXPOWER\" then\n        s.value = UnitPower(\"player\", s.type, aura_env.power.unmodified)\n        s.total = UnitPowerMax(\"player\", s.type, aura_env.power.unmodified)\n        s.value_original, s.total_original = s.value, s.total\n        s.short = AbbreviateNumbers(s.value, s.type)\n        \n    elseif event == \"UNIT_SPELLCAST_START\" then\n        local spellID = select(3, ...)\n        if spellID then\n            if aura_env.spells[spellID] and aura_env.spells[spellID].type == s.type then\n                local spell = aura_env.spells[spellID]\n                local direction = spell.power >= 0 and \"forward\" or \"backward\"\n                local direction_indicator = spell.power >= 0 and \"+\" or \"-\"\n                local modChange = aura_env.getPowerModifierAmounts(spellID)\n                local change = spell.power + modChange\n                \n                s.additionalProgress[2] = {direction=direction, width=change}\n                s.direction_indicator = direction_indicator\n                s.change = change\n                s.casting = true\n            else\n                local cost\n                local spellCostInfo\n                local costTable = C_Spell.GetSpellPowerCost(spellID);\n                if costTable then\n                    for _, costInfo in pairs(costTable) do\n                        if ( \n                            costInfo.requiredAuraID ~= 0 \n                            and costInfo.hasRequiredAura \n                            and costInfo.type == s.type\n                        )\n                        or (costInfo.requiredAuraID == 0 and costInfo.type == s.type)\n                        then\n                            spellCostInfo = costInfo\n                            cost = costInfo.cost\n                            break\n                        end\n                    end\n                    if cost then\n                        s.additionalProgress[1] = {direction=\"backward\", width=cost}\n                        s.change = -cost\n                        s.direction_indicator = \"-\"\n                        s.costInfo = spellCostInfo\n                        s.casting = true\n                    end\n                end\n            end\n        end\n        \n    elseif event == \"UNIT_SPELLCAST_STOP\" then\n        s.additionalProgress =  { {},{}, }\n        s.change = 0\n        s.casting = false\n        s.direction_indicator = nil\n    end\n    \n    --adjust for destro warlocks\n    if aura_env.power.unmodified then\n        s.value = s.value_original*0.1\n        s.total = s.total_original*0.1\n    end\n    \n    --Prediction calculation\n    if s.casting and s.change and s.change ~= 0 then\n        s.prediction = s.value + s.change\n    else\n        s.prediction = s.value\n    end\n    if s.type == Enum.PowerType.Mana then \n        s.prediction = s.prediction/s.total*100\n    end\n    if s.prediction > s.total then\n        s.prediction = s.total\n    end\n    \n    --ChangeText output format\n    if s.change < 0 and s.type == Enum.PowerType.Mana then \n        s.changeText = s.change ~= 0 and s.change/s.total*100 or 0\n    elseif s.change > 0 then\n        s.changeText = \"+\" .. s.change\n    else\n        s.changeText = s.change\n    end\n    \n    --Always do this stuff\n    s.percent = s.value/s.total*100 \n    s.changed = true\n    \n    return true\nend",
["event"] = "Power",
["customStacks"] = "function()\n    local current = aura_env.power.current or UnitPower(\"player\", aura_env.power.SpecPowerTypes.current)\n    local spellPower = aura_env.power.spellPower or 0\n    local max = UnitPowerMax(\"player\",aura_env.power.SpecPowerTypes.current)\n    local new = current+spellPower\n    \n    if new <= 0 then new = 0 end\n    if new > max then new = max end\n    aura_env.power.new = new;\n    \n    return new \nend",
["customDuration"] = "function()\n    local current = aura_env.power.current or UnitPower(\"player\",aura_env.power.SpecPowerTypes.current)\n    local max = UnitPowerMax(\"player\",aura_env.power.SpecPowerTypes.current)\n    return current,max,true\nend",
