["custom"] = "function(_allStates, _weakAuraEvent, _event, _id, _message, _expirationTime, _icon, _colors)\n    if _weakAuraEvent ~= \"BOSSMOD_TIMELINE\" then\n        return\n    end\n    \n    if _event == \"START\" then\n        if not _id then\n            return\n        end\n        \n        _allStates[_id] = _allStates[_id] or {}\n        \n        local state = _allStates[_id]\n        state.name = _message\n        state.icon = _icon\n        state.progressType = \"timed\"\n        state.expirationTime = _expirationTime\n        state.duration = _expirationTime - GetTime()\n        state.colorTable = _colors\n        state.c_offset = 0\n        state.autoHide = true\n        state.changed = true\n        state.show = true\n        \n        local now = GetTime()\n        if _expirationTime < now + aura_env.config.timelineEntryTime then\n            state.c_queued = false\n        else\n            state.c_queued = true\n            C_Timer.After(_expirationTime - now - aura_env.config.timelineEntryTime, function() WeakAuras.ScanEvents(\"BOSSMOD_TIMELINE\", \"REFRESH\", _id) end)\n        end\n    elseif _event == \"STOP\" then\n        if not _id then\n            return\n        end\n        \n        _allStates[_id] = _allStates[_id] or {}\n        \n        local state = _allStates[_id]\n        state.show = false\n        state.changed = true\n    elseif _event == \"STOP_ALL\" then\n        for id, state in pairs(_allStates) do\n            if not _id or tostring(_id) == strsplit(\"^\", id) then\n                state.show = false\n                state.changed = true\n            end\n        end\n    elseif _event == \"REFRESH\" then\n        if not _allStates[_id] then\n            return\n        end\n        \n        local timeLeft = (_allStates[_id].expirationTime or 0) - GetTime()\n        if (aura_env.config.timelineEntryTime or 0) - timeLeft > 0.5 or (aura_env.config.timelineEntryTime or 0) - timeLeft < -0.1 then\n            return\n        end\n        \n        _allStates[_id].c_offset = 0\n        _allStates[_id].c_queued = false\n        _allStates[_id].changed = true\n    end\n    \n    local queuedEvents = {}\n    local events = {}\n    \n    for id, state in pairs(_allStates) do\n        if state.c_queued then\n            table.insert(queuedEvents, { id = id, expirationTime = state.expirationTime })\n        else\n            table.insert(events, {id = id, expirationTime = state.expirationTime })\n        end\n    end\n    \n    table.sort(queuedEvents, function(a, b) return (a.expirationTime or 0) < (b.expirationTime or 0) end)\n    table.sort(events, function(a, b) return (a.expirationTime or 0) < (b.expirationTime or 0) end)\n    \n    for i, event in ipairs(queuedEvents) do\n        _allStates[event.id].c_offset = i * aura_env.data.spacing\n    end\n    \n    for i = #events, 1, -1 do\n        local event = events[i]\n        local lastEvent = events[i + 1]\n        \n        if lastEvent then\n            local timeDifference = (_allStates[lastEvent.id].expirationTime or 0) - (_allStates[event.id].expirationTime or 0)\n            local lastEventOffset = (_allStates[lastEvent.id].c_offset or 0)\n            \n            _allStates[event.id].c_offset =\n            aura_env.data.spacing\n            - (timeDifference\n                * aura_env.data.travelDistance\n                / aura_env.config.timelineEntryTime\n            - lastEventOffset)\n            \n            if _allStates[event.id].c_offset < 0 then\n                _allStates[event.id].c_offset = 0\n            end\n        end\n    end\n    \n    return true\nend",
