["translateFunc"] = "-- BossMod - Timeline - Icon - Animation - Slide - Custom Function\n\nfunction(_progress, _startX, _startY, _deltaX, _deltaY)\n    local endX, endY\n    \n    if not aura_env.state then\n        endX = _startX\n        endY = _startY\n    elseif aura_env.config.timeLimitForSpells > 0 and aura_env.state.expirationTime - GetTime() >= aura_env.config.timeLimitForSpells then\n        endX = _startX\n        endY = _startY + GetScreenHeight()\n    elseif aura_env.state.c_queued then\n        endX = _startX\n        if not aura_env.config.reverse then\n            endY = _startY + aura_env.state.c_offset\n        else\n            endY = _startY - aura_env.data.travelDistance - aura_env.state.c_offset\n        end\n    else\n        local progress = 1\n        \n        if aura_env.state.expirationTime then\n            progress = (aura_env.state.expirationTime - GetTime()) / aura_env.config.timelineEntryTime\n        end\n        \n        if progress > 1 then\n            progress = 1\n        elseif progress < 0 then\n            progress = 0\n        end\n        \n        endX = _startX\n        if not aura_env.config.reverse then\n            endY = _startY - ((1 - progress) * aura_env.data.travelDistance) - (aura_env.state.c_offset or 0)\n        else\n            endY = _startY - ((0 + progress) * aura_env.data.travelDistance) - (aura_env.state.c_offset or 0)\n        end\n    end\n    \n    return endX, endY\nend",
