garian\n    aura_env.resetText = \"Töröl\"\nend\nif aura_env.useEng then \n    aura_env.resetText = \"Reset\" \nend\n\nlocal values = {9, 9, 9, 9}\nlocal message = string.format(\"%d %d %d %d\", values[1], values[2], values[3], values[4])\nlocal name, _ = UnitName(\"player\")\nif not aura_env.frameData then\n    local b = CreateFrame(\"Button\", nil, aura_env.region )\n    b:SetAllPoints(aura_env.region )\n    b:RegisterForClicks(\"LeftButtonDown\")\n    aura_env.frameData = b\nend\nif aura_env.noCommunication then\n    aura_env.frameData:SetScript(\"OnClick\", function()\n            if debug then print(\"Click Registered - true\") end\n            C_ChatInfo.RegisterAddonMessagePrefix(\"Tirna Ver2\")\n            if debug then \n                print(\"noCommunication sent - \", C_ChatInfo.SendAddonMessageLogged(\"Tirna Ver2\", message, \"WHISPER\", name)) \n            else\n                C_ChatInfo.SendAddonMessageLogged(\"Tirna Ver2\", message, \"WHISPER\", name)\n            end\n            WeakAuras.ScanEvents(\"GlowReset\")            \n    end)\nelse\n    aura_env.frameData:SetScript(\"OnClick\", function()\n            if debug then print(\"Click Registered - true\") end\n            C_ChatInfo.RegisterAddonMessagePrefix(\"Tirna Ver2\")\n            if IsPartyLFG() then            \n                if debug then \n                    print(\"LFG sent - \", C_ChatInfo.SendAddonMessageLogged(\"Tirna Ver2\", message, \"INSTANCE_CHAT\")) \n                else\n                    C_ChatInfo.SendAddonMessageLogged(\"Tirna Ver2\", message, \"INSTANCE_CHAT\")\n                end\n            elseif UnitInParty(\"player\") then            \n                if debug then \n                    print(\"party sent - \", C_ChatInfo.SendAddonMessageLogged(\"Tirna Ver2\", message, \"PARTY\")) \n                else\n                    C_ChatInfo.SendAddonMessageLogged(\"Tirna Ver2\", message, \"PARTY\")\n                end\n            else               \n       