["check"] = "event",
["custom"] = "function(states, event, ...)\n    if event == \"STATUS\" then\n        local current_stacks = 0\n        local max_stacks = 10 \n         \n        \n        for i = 1, max_stacks do\n            states[i] = {\n              show = true,\n              changed = true,\n              value = current_stacks >= i and 1 or 0,\n              total = 1,\n              progressType = \"static\",\n              index = i,\n              stacks = current_stacks,\n              maelstrom = i\n              }\n        end\n        \n        \n        return true\n    end\n    \n    if event == \"UNIT_AURA\" then\n        \n        local current_stacks = 0\n        local max_stacks = 10  \n        \n        \n        local aura = C_UnitAuras.GetPlayerAuraBySpellID(344179)\n        \n        if aura ~= nil then\n            if aura.applications then\n                current_stacks = aura.applications\n            else\n                current_stacks = 1\n            end\n        end\n        \n        if current_stacks and max_stacks then\n            local has_changes = false\n            \n            for i = 1, max_stacks do\n                local state = states[i]\n                if not state then break end\n                if state.stacks == current_stacks then\n                    return false\n                end\n                \n                if state then\n                    if state.stacks ~= current_stacks then\n                        state.stacks = current_stacks\n                        state.changed = true\n                        has_changes = true\n                    end\n                    \n                    local value = current_stacks >= i and 1 or 0\n                    \n                    if value ~= state.value then\n                        state.value = value\n                        state.changed = true\n                        has_changes = true\n                    end\n                end\n            end\n            \n            return has_changes\n        end\n        \n        return false\n    end\nend",
