["value"] = "function(states)\n    if not aura_env.SUI_vdh_opener then\n        local fury = (states[2] and states[2].power) or 0    \n        local soulfrag = (states[3] and states[3].stacks) or 0\n        \n        -- CAST SPIRIT BOMB\n        -- If power is 40 or more, change the icon\n        if not aura_env.SUI_vdh_spiritBomb then\n            if fury >= 40 then\n                if aura_env.state then\n                    aura_env.state.icon = states[5].icon -- Spirit Bomb icon\n                    return true\n                end\n            end\n            \n            if states[4].spellId ~= nil then\n                if states[4].spellId == 247454 then          \n                    aura_env.SUI_vdh_spiritBomb = true\n                end\n            end \n        end\n        -- END SPIRIT BOMB\n        \n        -- CAST IMMOLATION AURA\n        if not aura_env.SUI_vdh_immolationAura and aura_env.SUI_vdh_spiritBomb then\n            if aura_env.state then\n                aura_env.state.icon = states[6].icon -- Immolation Aura icon\n                return true\n            end\n            \n            if states[4].spellId ~= nil then\n                if states[4].spellId == 258920 then\n                    aura_env.SUI_vdh_immolationAura = true\n                end\n            end \n        end\n        -- END IMMOLATION AURA\n        \n        -- CAST FEL DEVASTATION\n        -- If power is 50 or more, change the icon\n        if not aura_env.SUI_vdh_felDevastation and aura_env.SUI_vdh_immolationAura then\n            if fury >= 50 then\n                if aura_env.state then\n                    aura_env.state.icon = states[7].icon -- FEL DEVASTATION icon\n                    return true\n                end\n            end\n            \n            if states[4].spellId ~= nil then\n                if states[4].spellId == 212084 then\n                    aura_env.SUI_vdh_felDevastation = true\n                end\n            end \n        end\n  